import { Checkbox, FormControlLabel, Slider, TextField } from '@mui/material'

const TranslationControl = ({
	translation,
	setTranslation,
	randomize,
	setRandomize,
}) => {

	const handleTranslationChange = (key, value) => {
		setTranslation({ ...translation, [key]: value })
	}

	return (
		<>
			{['pushPull', 'headTail', 'footHead'].map((key) => (
				<div key={key} className='translation-control'>
					<TextField
						label={key.charAt(0).toUpperCase() + key.slice(1)}
						type='number'
						value={translation[key]}
						onChange={(e) =>
							handleTranslationChange(key, Number(e.target.value))
						}
						size='small'
						className='translation-textfield'
					/>
					<Slider
						value={translation[key]}
						onChange={(e, newValue) => handleTranslationChange(key, newValue)}
						aria-labelledby={`${key}-slider`}
						className='translation-slider'
					/>
					<FormControlLabel
						control={
							<Checkbox
								checked={randomize}
								onChange={(e) => setRandomize(e.target.checked)}
							/>
						}
						label='Randomize'
					/>
				</div>
			))}
		</>
	)
}

export default TranslationControl
