import React, { useRef, useEffect } from 'react';
import * as THREE from 'three';
import nifti from 'nifti-reader-js';

const ThreeJS = ({ rotation, tilt, translation }) => {
  const mountRef = useRef(null);

  useEffect(() => {
    const scene = new THREE.Scene();
    scene.background = new THREE.Color(0xeeeeee);

    const camera = new THREE.PerspectiveCamera(75, mountRef.current.clientWidth / mountRef.current.clientHeight, 0.1, 1000);
    camera.position.z = 5;

    const renderer = new THREE.WebGLRenderer();
    renderer.setSize(mountRef.current.clientWidth, mountRef.current.clientHeight);
    mountRef.current.appendChild(renderer.domElement);

    // Load the NIfTI file
    const loadNiftiFile = (url) => {
      fetch(url)
        .then(response => response.arrayBuffer())
        .then(buffer => {
          const niftiHeader = nifti.readHeader(new Uint8Array(buffer));
          const niftiData = nifti.readImage(niftiHeader, new Uint8Array(buffer));

          const width = niftiHeader.dims[1];
          const height = niftiHeader.dims[2];
          const depth = niftiHeader.dims[3];

          // Create a 1D array for the texture
          const textureData = new Uint8Array(width * height * depth);

          for (let z = 0; z < depth; z++) {
            for (let y = 0; y < height; y++) {
              for (let x = 0; x < width; x++) {
                const index = x + y * width + z * width * height;
                textureData[index] = niftiData[index]; // Assuming niftiData is in the right format
              }
            }
          }

          const texture = new THREE.DataTexture(textureData, width, height, THREE.RedFormat);
          texture.needsUpdate = true;

          const material = new THREE.MeshBasicMaterial({
            map: texture,
            transparent: true,
            opacity: 0.5,
          });

          const geometry = new THREE.BoxGeometry(width, height, depth);
          const mesh = new THREE.Mesh(geometry, material);
          scene.add(mesh);

          const animate = () => {
            requestAnimationFrame(animate);

            mesh.rotation.y = rotation;
            mesh.rotation.x = tilt;

            mesh.position.x = translation.pushPull;
            mesh.position.y = translation.raiseLower;
            mesh.position.z = translation.footHead;

            renderer.render(scene, camera);
          };

          animate();
        })
        .catch(error => console.error('Error loading NIfTI file:', error));
    };

    loadNiftiFile('CT_AVM.nii.gz');

    return () => {
      mountRef.current.removeChild(renderer.domElement);
    };
  }, [rotation, tilt, translation]);

  return <div ref={mountRef} style={{ width: '100%', height: '100%' }}></div>;
};

export default ThreeJS;
