import React, { useEffect, useRef, useState, useCallback } from 'react';
import * as nifti from 'nifti-reader-js';

const ThreeJS = ({ rotation, tilt, translation, file }) => {
    const canvasRef = useRef(null);
    const [niftiHeader, setNiftiHeader] = useState(null);
    const [niftiImage, setNiftiImage] = useState(null);
    const [slice, setSlice] = useState(0);

    const readNIFTI = useCallback((data) => {
        console.log("Attempting to read NIFTI data");
        try {
            if (nifti.isCompressed(data)) {
                console.log("Data is compressed, decompressing...");
                data = nifti.decompress(data);
            }

            if (nifti.isNIFTI(data)) {
                console.log("Valid NIFTI data found");
                const header = nifti.readHeader(data);
                const image = nifti.readImage(header, data);

                console.log("Header:", header);
                console.log("Image data length:", image.length);

                setNiftiHeader(header);
                setNiftiImage(image);

                const slices = header.dims[3];
                setSlice(Math.round(slices / 2));
            } else {
                console.error("Not a valid NIFTI file");
            }
        } catch (error) {
            console.error("Error reading NIFTI file:", error);
        }
    }, []);

    useEffect(() => {
        if (file) {
            console.log("File selected:", file.name);
            const reader = new FileReader();
            reader.onload = (e) => {
                console.log("File loaded, calling readNIFTI");
                const arrayBuffer = e.target.result;
                readNIFTI(arrayBuffer);
            };
            reader.onerror = (e) => {
                console.error("Error reading file:", e);
            };
            reader.readAsArrayBuffer(file);
        }
    }, [file, readNIFTI]);

    const drawCanvas = useCallback((sliceIndex) => {
        console.log("Drawing canvas for slice:", sliceIndex);
        if (!niftiHeader || !niftiImage) {
            console.log("niftiHeader or niftiImage is null, cannot draw canvas");
            return;
        }

        const canvas = canvasRef.current;
        const cols = niftiHeader.dims[1];
        const rows = niftiHeader.dims[2];
        canvas.width = cols;
        canvas.height = rows;
        const ctx = canvas.getContext('2d');
        const imageData = ctx.createImageData(cols, rows);

        const sliceSize = cols * rows;
        const sliceOffset = sliceSize * sliceIndex;
        const typedData = new Float32Array(niftiImage);

        let min = Infinity;
        let max = -Infinity;
        for (let i = 0; i < sliceSize; i++) {
            const value = typedData[sliceOffset + i];
            min = Math.min(min, value);
            max = Math.max(max, value);
        }

        console.log("Min value:", min, "Max value:", max);

        for (let i = 0; i < sliceSize; i++) {
            const value = typedData[sliceOffset + i];
            const normalizedValue = Math.round(((value - min) / (max - min)) * 255);
            const index = i * 4;
            imageData.data[index] = normalizedValue;
            imageData.data[index + 1] = normalizedValue;
            imageData.data[index + 2] = normalizedValue;
            imageData.data[index + 3] = 255;
        }

        ctx.putImageData(imageData, 0, 0);

        // Apply rotation, tilt, and translation
        ctx.save();
        ctx.translate(cols / 2, rows / 2);
        ctx.rotate(rotation * Math.PI / 180);
        ctx.translate(-cols / 2, -rows / 2);
        ctx.translate(translation.pushPull, translation.raiseLower);
        ctx.drawImage(canvas, 0, 0);
        ctx.restore();

        console.log("Canvas drawing completed");
    }, [niftiHeader, niftiImage, rotation, tilt, translation]);

    useEffect(() => {
        if (niftiHeader && niftiImage) {
            console.log("niftiHeader and niftiImage updated, drawing canvas");
            drawCanvas(slice);
        }
    }, [niftiHeader, niftiImage, slice, drawCanvas]);

    return (
        <div>
            <canvas ref={canvasRef}></canvas>
            {niftiHeader && (
                <input
                    type="range"
                    min="0"
                    max={niftiHeader.dims[3] - 1}
                    value={slice}
                    onChange={(e) => setSlice(parseInt(e.target.value))}
                />
            )}
        </div>
    );
};

export default ThreeJS;
